const string dpTemperature = dpRef + &quot;.Temperature&quot;;

//const string dpFE1_Status = dpRef + &quot;.FE1_Status&quot;;
//const string dpFE2_Status = dpRef + &quot;.FE2_Status&quot;;
//const string dpFE3_Status = dpRef + &quot;.FE3_Status&quot;;
